
public class forloopingnumber {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i,y,x,a=0;
		for(i=1;i<=5;i++){
			
			for(y=1;y<=5-i;y++){
				
				System.out.print(" ");}
			
				for(x=1;x<=(i*2)-1;x++){
					a++;
					System.out.print(a);
					
				
			}
			System.out.print("\n");
				
			}
			
		}
			

			}
